import React from "react"
import Modal from 'react-modal';
import './AddProd.css'
import axios from "axios";

export default function AddProd(){
    const [modalIsOpen, setIsOpen] = React.useState(false);
    const [prodInfo,setprodInfo] = React.useState({
      image:'',
      name:'',
      price:'',
      description:''
    })

    function openModal() {
      setIsOpen(true);
    }
      
    function closeModal() {
      setIsOpen(false);
    }
    
    async function handleSubmit(e){
      e.preventDefault()
      try{
        await axios.post("http://localhost:3002/products", prodInfo)
        setprodInfo({
          image:'',
          name:'',
          price:'',
          description:''
        })
      }catch(err){
        console.log(err.message)
      }
    }

    function handleInput(e){
      setprodInfo({
        ...prodInfo,
        [e.target.name]: e.target.value
      })
    }

    return(
        <div>
          <button onClick={openModal}>Add product</button>
          <Modal
            isOpen={modalIsOpen}
            onRequestClose={closeModal}
            ariaHideApp={false}
            className='modalBG'
          >
            <button onClick={closeModal} className='closeButton'>X</button>
            <form onSubmit={(e) => handleSubmit(e)}  className="OverlayModal">
              <input className="inputST" type="text" onChange={(e) => handleInput(e)} placeholder="url of the image" />
              <input className="inputST" type="text" onChange={(e) => handleInput(e)} placeholder="name of the product" />
              <input className="inputST" type="text" onChange={(e) => handleInput(e)} placeholder="price of the product" />
              <input className="inputST" type="text" onChange={(e) => handleInput(e)} placeholder="description of the product" />
              <button type="submit">upload</button>
            </form>
            
          </Modal>
        </div>
    )
}
