const initialState = {
    products:[],

}
    
export const reducer = (state = initialState, action) => {
      switch (action.type) {
          case 'GET_PRODUCTS':
            return {
              ...state, 
              products:action.payload,     
            }
          case 'DISPATCH_PRODUCTS':
            return action.payload
          default:
              return state;
        }
}
