const initialState = {
    products:[],

}
    
export const reducer = (state = initialState, action) => {
      switch (action.type) {
          case 'GET_PRODUCTS':
            return {
              ...state, 
              products:action.payload,     
            }
          case 'DISPATCH_PRODUCTS':
            return state
          default:
              return state;
        }
}

export async function dispatchProducts(dispatch, getState){
  // let info = await axios(urlapi)
  // return {type: "GET_PRODUCTS", payload:info.data}
  await fetch("http://localhost:3002/products", {
      method: 'GET',
    })
    .then( res => res.json())
    .then(data => {dispatch ({type: 'DISPATCH_PRODUCTS', payload:data})})
   
  // console.log(info)
  
}
